import React from 'react'
import "./stylo.css"
import { useForm } from "./useForm";

const initialForm = {
    name: "",
    email: "",
    subject: "",
    comments: "",
};

const validationsForm = (form) => {
    let errors = {};
    let regexName = /^[A-Za-zÑñÁáÉéÍíÓóÚúÜü\s]+$/;
    let regexEmail = /^(\w+[/./-]?){1,}@[a-z]+[/.]\w{2,}$/;
    let regexComments = /^.{1,255}$/;

    if (!form.name.trim()) {
        errors.name = "El campo 'Nombre' es requerido";
    } else if (!regexName.test(form.name.trim())) {
        errors.name = "El campo 'Nombre' sólo acepta letras y espacios en blanco";
    }

    if (!form.email.trim()) {
        errors.email = "El campo 'Email' es requerido";
    } else if (!regexEmail.test(form.email.trim())) {
        errors.email = "El campo 'Email' es incorrecto";
    }

    if (!form.subject.trim()) {
        errors.subject = "El campo 'Asunto a tratar' es requerido";
    }

    if (!form.comments.trim()) {
        errors.comments = "El campo 'Comentarios' es requerido";
    } else if (!regexComments.test(form.comments.trim())) {
        errors.comments = "El campo 'Comentarios' no debe exceder los 255 caracteres";
    }

    return errors;
};

const Contacto = () => {
    const {
        form,
        errors,
        response,
        handleChange,
        handleBlur,
        handleSubmit,
    } = useForm(initialForm, validationsForm);

    return (
        <div className='contacto'>
            <h2>Formulario de Contacto</h2>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    name="name"
                    placeholder="Escribe tu nombre"
                    onBlur={handleBlur}
                    onChange={handleChange}
                    value={form.name}
                    required
                />
                {errors.name && <p>{errors.name}</p>}

                <input
                    type="email"
                    name="email"
                    placeholder="Escribe tu email"
                    onBlur={handleBlur}
                    onChange={handleChange}
                    value={form.email}
                    required
                />
                {errors.email && <p>{errors.email}</p>}

                <input
                    type="text"
                    name="subject"
                    placeholder="Asunto a tratar"
                    onBlur={handleBlur}
                    onChange={handleChange}
                    value={form.subject}
                    required
                />
                {errors.subject && <p>{errors.subject}</p>}

                <textarea
                    name="comments"
                    cols="50"
                    rows="5"
                    placeholder="Escribe tus comentarios"
                    onBlur={handleBlur}
                    onChange={handleChange}
                    value={form.comments}
                    required
                ></textarea>
                {errors.comments && <p>{errors.comments}</p>}

                <input type="submit" value="Enviar" />
            </form>

            {response && <p>Los datos han sido enviados.</p>}
            <ul className='lista'>
            <li><img src="data:image/png;base64," alt="" srcset="" />Facebook: <a href="https://www.facebook.com/imperio.yugioh">Imperio YUGIOH</a></li>
            <li><img src="data:image/jpeg;base64," alt="" srcset="" />Instagram: <a href="https://www.instagram.com/imperio_yugioh/">Imperio YUGIOH</a></li>
            <li><img src="data:image/png;base64," alt="" srcset="" />Twitter: <a href="https://twitter.com/ImperioYugioh">Imperio YUGIOH</a></li>
            <li><img src="data:image/png;base64," alt="" srcset="" />Youtube: <a href="https://www.youtube.com/@imperio_yugioh">Imperio YUGIOH</a></li>
        </ul>
        </div>
    );
};

export default Contacto;